      fixture.componentInstance._settings.order = Math.floor(Math.random() * 100);
      parent.chart.data = { datasets: [] };
      //
      fixture.detectChanges();
      //
      const added = parent.chart.data.datasets[0];
      expect(added.order).toBe(fixture.componentInstance._settings.order);
   });
